mapper.delete:
delete from MERCHANT_CHANNEL
where MCODE = #{mcode,jdbcType=VARCHAR}
  and CHANNEL_ID = #{channelId,jdbcType=DECIMAL}

mapper.update:
update MERCHANT_CHANNEL
set CREATE_USER = #{createUser,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  EDITOR_USER = #{editorUser,jdbcType=VARCHAR},
  EDIT_TIME = #{editTime,jdbcType=TIMESTAMP},
  STATUS = #{status,jdbcType=DECIMAL},
  REMARK = #{remark,jdbcType=VARCHAR}
where MCODE = #{mcode,jdbcType=VARCHAR}
  and CHANNEL_ID = #{channelId,jdbcType=DECIMAL}

mapper.select:
	    select count(*)
	    from merchant_channel ac
	    left join merchant b
	    on ac.mcode = b.mcode
	    left join channel c
	    on ac.channel_id = c.channel_id



         ac. mcode= #{mcode,jdbcType=VARCHAR}



           AND ac.CHANNEL_ID =#{channelId,jdbcType=DECIMAL}


          AND ac.status=#{status,jdbcType=DECIMAL}

select pms.mcode as mcode, b.mer_Name as merName, c.channel_id as channelId, c.channel_name as channelName, c.pay_type as payType, c.pay_Channel_Type as payChannelType, e.name as name, pms.create_time as createTime from merchant_channel pms left join merchant b on pms.mcode = b.mcode left join channel c on pms.channel_id = c.channel_id left join channel_type e on e.pay_Channel_Type = c.pay_Channel_Type
pms. mcode= #{mcode,jdbcType=VARCHAR}
AND pms.CHANNEL_ID =#{channelId,jdbcType=DECIMAL}
AND pms.status=#{status,jdbcType=DECIMAL}
