mapper.select:
	SELECT COUNT(*) FROM ORGANIZATION O


			AND O.PARENT_ID = #{parentId}



    			AND O.ORG_ID <> #{noId}



			AND O.NAME = #{name}

mapper.update:
UPDATE ORGANIZATION SET
	STATUS = ${status}, CHECK_USER = #{checkUser}


		    CHECK_DESC = #{checkDesc}


WHERE ORG_ID IN (${ids})

mapper.select:
select count(1) from ORGANIZATION O WHERE O.DELETED = 0 AND (O.STATUS <> 1)
AND O.PARENT_ID = #{parentId}
AND REVERSE(O.LAYER) LIKE REVERSE('${layer}%')

select
from ORGANIZATION O LEFT JOIN ORG_CLEANING OCL ON OCL.ORG_ID = O.ORG_ID LEFT JOIN ORG_CONTRACT OCO ON OCO.ORG_ID = O.ORG_ID LEFT JOIN ORGANIZATION OO ON OO.ORG_ID = O.PARENT_ID WHERE O.DELETED = 0 AND (O.STATUS <> 1)
AND O.PARENT_ID = #{parentId}
AND REVERSE(O.LAYER) LIKE REVERSE('${layer}%')
ORDER BY O.ORG_ID ASC

mapper.update:
UPDATE ORGANIZATION SET
	type = #{type}
WHERE LAYER LIKE '${layer}'||'A%'
